import {createS} from '@reduxjs/toolkit';



const Cartsl = createslice({

name:'cart',
initialState,
reducers:{
    addtocart:(state,action) =>{
const newItem = action.payload
const existingItem = state.cartItems.find((items)=>items._id === newItem._id)
if(existingItem){
    existingItem.quantity += newItem.quantity
}else{
    state.cartItems.push({
        _id:newItem._id,
        title:newItem.title,
        image:newItem.image,
        ds:newItem.ds,
        price:newItem.price,
        quantity:newItem.quantity
    });
}
localStorage.setItem("cartItems",JSON.stringify(state.cartItems))
    },
    deleteFromCart:(state,action)=>{
        state.cartItems = state.cartItems.filter((items)=> items._id !== action.payload._id)
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems))
    },
    updateQuantity : (state,action)=>{
        const{id,quantity} = action.payload
        const itemToUpdate = state.cartItems.find((items)=>items._id === id)
        if(itemsToUpdate){
            itemToUpdate.quantity = quantity
        };
        localStorage.setItem("cartItems",JSON.stringify(state.cartItems))
    }

}

}
)

export default Cartsl.reducer;
export const{addtocart,deleteFromCart,updateQuantity} = Cartsl.actions
